import { useState } from "react";
import InputNames from "../components/InputNames";

export default function Home(){

  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = async (event, names, emails, setError) => {

    const participantArray = [];
    for (let i = 0; i < names.length; i++) {
      const name = names[i];
      const email = emails[i];
      const participantObject = { name, email };
      participantArray.push(participantObject);
    }
    console.log(participantArray)

    if(!submitted){
      event.preventDefault();

    const namesList = {
      namesList: names
    }

    const response = await fetch("/api/assignments/generate", {
        method: 'POST',
        body: JSON.stringify(namesList),
        headers: {
            'Content-Type': 'application/json'
        }
    })

    const json = await response.json()

    if (!response.ok) {
      setError(json.error)
    }
    if (response.ok) {

      const emailTemplates = [];
      json.forEach((element) => {
        const { to, from } = element;
        emailTemplates.push({to, from})
      });
      console.log(emailTemplates)

      // create email template objects {emailTo, respectiveAssignment} and make call to /send-emails. Each email will contain who each person is buying for


      setSubmitted(true);
    }
    }


  }

  if (!submitted){
    return (
      <div className="home">
        <div className = "input-names">
          <InputNames onSubmit = {handleSubmit}></InputNames>
        </div>
        <div className="footer"></div>
      </div>
    );
  }else{
    return (
      <div className="home">
        <div className = "input-names">
          <InputNames onSubmit = {handleSubmit}></InputNames>
        </div>
        <div>
          <p>Submitted!</p>
        </div>
        <div className="footer"></div>
      </div>
    );
  }
}
